# Desafio Class Abstratct

from abc import ABC, abstractmethod

class Monitor(ABC):

    @abstractmethod
    def aumentar_claridade(self,valor):
       pass

    @abstractmethod
    def diminuir_claridade(self,valor):
        pass


class MonitorFullHD(Monitor):
    def aumentar_claridade(self,valor):
        print(f'aumenter o valor da claridade {valor} nivel')

    def diminuir_claridade(self,valor):
        print(f'diminuir o valor da claridade {valor} nivel')


monitor = MonitorFullHD()
monitor.aumentar_claridade(10)
monitor.diminuir_claridade(10)
